const searchUsersOrder=()=>{const e=document.getElementById("learn-press-order");if(null===e)return;const t=e.querySelectorAll(".change-user");if(0===t.length)return;const n=document.getElementById("modal-search-users");if(null===n)return;t.forEach(e=>{e.addEventListener("click",t=>{t.preventDefault();e.dataset.multiple;n.classList.add("active"),r()})});const r=()=>{const e=n.querySelector(".close-modal");null!==e&&e.addEventListener("click",e=>{e.preventDefault(),n.classList.remove("active")})}};document.addEventListener("DOMContentLoaded",(function(e){searchUsersOrder()}));